// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IXershaPool
 * @notice Base interface for all Xersha pool types
 * @dev Common interface elements shared across ROSCA, Savings, and Donation pools
 */
interface IXershaPool {
    /**
     * @notice Returns the address of the circle (Lens.xyz contract) this pool belongs to
     * @return The circle contract address
     */
    function circleId() external view returns (address);

    /**
     * @notice Returns the address of the user who created this pool
     * @return The creator's address
     */
    function creator() external view returns (address);

    /**
     * @notice Returns the human-readable name of the circle
     * @return The circle name
     */
    function circleName() external view returns (string memory);

    /**
     * @notice Returns whether the pool is currently active
     * @return True if pool is active, false otherwise
     */
    function isActive() external view returns (bool);

    /**
     * @notice Returns the list of all members in the pool
     * @return Array of member addresses
     */
    function getMembers() external view returns (address[] memory);

    /**
     * @notice Returns the total number of members in the pool
     * @return Member count
     */
    function getMemberCount() external view returns (uint256);
}

contracts/libraries/TokenTransfer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title TokenTransfer
 * @notice Library for handling both native token and ERC20 token transfers
 * @dev Reduces code duplication across pool contracts
 */
library TokenTransfer {
    /**
     * @notice Receives tokens from sender (either native or ERC20)
     * @param tokenAddress Address of the ERC20 token (zero address if native)
     * @param isNativeToken Whether this is a native token transfer
     * @param amount Amount of tokens to receive (for ERC20) or 0 (for native)
     * @return The actual amount received
     */
    function receiveTokens(
        address tokenAddress,
        bool isNativeToken,
        uint256 amount
    ) internal returns (uint256) {
        if (isNativeToken) {
            require(msg.value > 0, "Must send native token");
            return msg.value;
        } else {
            require(msg.value == 0, "No native token");
            IERC20(tokenAddress).transferFrom(msg.sender, address(this), amount);
            return amount;
        }
    }

    /**
     * @notice Sends tokens to recipient (either native or ERC20)
     * @param tokenAddress Address of the ERC20 token (zero address if native)
     * @param isNativeToken Whether this is a native token transfer
     * @param recipient Address to send tokens to
     * @param amount Amount of tokens to send
     */
    function sendTokens(
        address tokenAddress,
        bool isNativeToken,
        address recipient,
        uint256 amount
    ) internal {
        if (isNativeToken) {
            (bool success, ) = payable(recipient).call{value: amount}("");
            require(success, "Transfer failed");
        } else {
            IERC20(tokenAddress).transfer(recipient, amount);
        }
    }
}

contracts/pools/DonationPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../interfaces/IXershaPool.sol";
import "../libraries/TokenTransfer.sol";

/**
 * @title DonationPool
 * @notice Fundraising pool where circle members donate toward a specific goal and beneficiary
 * @dev Includes refund mechanism if goal is not met by deadline
 */
contract DonationPool is IXershaPool, ReentrancyGuard, Pausable {
    using TokenTransfer for address;

    // ========== State Variables ==========

    /// @notice Address of the user who created this pool
    address public creator;

    /// @notice Address of the backend manager (can invite members)
    address public backendManager;

    /// @notice Address of the Lens.xyz circle contract
    address public circleId;

    /// @notice Human-readable name of the circle
    string public circleName;

    /// @notice Address of the ERC20 token used for donations (zero address if native token)
    address public tokenAddress;

    /// @notice Whether this pool uses native token (cBTC) or ERC20 token
    bool public isNativeToken;

    /// @notice Address that will receive the donated funds
    address public beneficiary;

    /// @notice Target fundraising amount in wei
    uint256 public goalAmount;

    /// @notice Deadline for fundraising as Unix timestamp
    uint256 public deadline;

    /// @notice Total amount raised so far
    uint256 public totalRaised;

    /// @notice Individual donation amounts per member
    mapping(address => uint256) public donations;

    /// @notice Array of all members in the pool
    address[] public members;

    /// @notice Mapping to check if an address is a member
    mapping(address => bool) public isMember;

    /// @notice Mapping to check if an address has been invited
    mapping(address => bool) public isInvited;

    /// @notice Array of addresses that have donated
    address[] public donors;

    /// @notice Whether funds have been released to beneficiary
    bool public fundsReleased;

    /// @notice Whether the pool is currently active
    bool public isActive;

    /// @notice Whether refunds are enabled (goal not met by deadline)
    bool public refundsEnabled;

    /// @notice Whether this contract has been initialized (for clone pattern)
    bool private initialized;

    // ========== Events ==========

    event PoolCreated(
        address indexed circleId,
        address indexed creator,
        address beneficiary,
        uint256 goal,
        uint256 deadline
    );
    event MemberInvited(address indexed member, address indexed invitedBy);
    event MemberJoined(address indexed member);
    event DonationMade(address indexed donor, uint256 amount);
    event GoalReached(uint256 totalRaised);
    event FundsReleased(address indexed beneficiary, uint256 amount);
    event RefundsEnabled(uint256 timestamp);
    event RefundClaimed(address indexed donor, uint256 amount);

    // ========== Modifiers ==========

    modifier onlyCreator() {
        require(msg.sender == creator, "Only creator can call this");
        _;
    }

    modifier onlyCreatorOrBackend() {
        require(
            msg.sender == creator || msg.sender == backendManager,
            "Only creator or backend"
        );
        _;
    }

    modifier onlyMember() {
        require(isMember[msg.sender], "Not a member");
        _;
    }

    modifier onlyInvited() {
        require(isInvited[msg.sender], "Not invited");
        _;
    }

    // ========== Constructor ==========

    /**
     * @notice Constructor for implementation contract
     * @dev Prevents the implementation contract from being initialized
     */
    constructor() {
        initialized = true;
    }

    /**
     * @notice Initializes a new Donation pool clone
     * @dev This replaces the constructor for cloned instances
     * @param _creator Address of the user creating the pool
     * @param _circleId Address of the Lens.xyz circle contract
     * @param _circleName Name of the circle
     * @param _backendManager Address of the backend manager (can invite members)
     * @param _beneficiary Address that will receive the funds
     * @param _goalAmount Target fundraising amount in wei
     * @param _deadline Deadline as Unix timestamp
     * @param _tokenAddress Address of the ERC20 token to use for donations (zero address if native)
     * @param _isNativeToken Whether this pool uses native token (cBTC) or ERC20 token
     */
    function initialize(
        address _creator,
        address _circleId,
        string memory _circleName,
        address _backendManager,
        address _beneficiary,
        uint256 _goalAmount,
        uint256 _deadline,
        address _tokenAddress,
        bool _isNativeToken
    ) external {
        require(!initialized, "Already initialized");
        initialized = true;

        require(_beneficiary != address(0), "Invalid beneficiary");
        require(_goalAmount > 0, "Goal must be positive");
        require(_deadline > block.timestamp, "Deadline must be future");
        require(_backendManager != address(0), "Invalid backend manager");

        // Validate token address based on token type
        if (_isNativeToken) {
            require(_tokenAddress == address(0), "Token address must be zero for native token");
        } else {
            require(_tokenAddress != address(0), "Invalid token address for ERC20");
        }

        creator = _creator;
        backendManager = _backendManager;
        circleId = _circleId;
        circleName = _circleName;
        beneficiary = _beneficiary;
        goalAmount = _goalAmount;
        deadline = _deadline;
        tokenAddress = _tokenAddress;
        isNativeToken = _isNativeToken;
        isActive = true;

        // Creator automatically becomes a member
        members.push(_creator);
        isMember[_creator] = true;
        isInvited[_creator] = true;

        emit PoolCreated(_circleId, _creator, _beneficiary, _goalAmount, _deadline);
        emit MemberJoined(_creator);
    }

    // ========== Member Management ==========

    /**
     * @notice Invites a new member to the pool
     * @dev Creator or backend manager can invite members
     * @param member Address of the member to invite
     */
    function inviteMember(address member) external onlyCreatorOrBackend whenNotPaused {
        require(!isInvited[member], "Already invited");
        require(member != address(0), "Invalid address");

        isInvited[member] = true;
        emit MemberInvited(member, msg.sender);
    }

    /**
     * @notice Allows an invited member to join the pool
     */
    function joinPool() external onlyInvited whenNotPaused {
        require(!isMember[msg.sender], "Already a member");

        members.push(msg.sender);
        isMember[msg.sender] = true;

        emit MemberJoined(msg.sender);
    }

    // ========== Donations ==========

    /**
     * @notice Allows anyone to donate to the fundraising pool
     * @dev For ERC20: Donor must have approved the contract to spend tokens before calling
     *      For native token: amount parameter is ignored, msg.value is used
     *      NOTE: Unlike SavingsPool and ROSCAPool, DonationPool allows public donations
     * @param amount Amount of ERC20 tokens to donate (ignored for native token pools)
     */
    function donate(uint256 amount) external payable whenNotPaused nonReentrant {
        require(block.timestamp <= deadline, "Deadline passed");
        require(!fundsReleased, "Funds already released");
        require(!refundsEnabled, "Refunds enabled, cannot donate");
        require(isActive, "Pool not active");
        require(isNativeToken || amount > 0, "Must donate something");

        uint256 donationAmount = TokenTransfer.receiveTokens(tokenAddress, isNativeToken, amount);

        // Track first-time donors
        if (donations[msg.sender] == 0) {
            donors.push(msg.sender);
        }

        donations[msg.sender] += donationAmount;
        totalRaised += donationAmount;

        emit DonationMade(msg.sender, donationAmount);

        // Check if goal reached
        if (totalRaised >= goalAmount) {
            emit GoalReached(totalRaised);
        }
    }

    // ========== Fund Management ==========

    /**
     * @notice Releases funds to the beneficiary
     * @dev Only creator can release, can only release after goal met or deadline passed
     */
    function releaseFunds() external onlyCreator whenNotPaused nonReentrant {
        require(!fundsReleased, "Already released");
        require(!refundsEnabled, "Refunds enabled");
        require(
            totalRaised >= goalAmount || block.timestamp > deadline,
            "Goal not met and deadline not passed"
        );

        fundsReleased = true;
        isActive = false;

        uint256 amount = isNativeToken
            ? address(this).balance
            : IERC20(tokenAddress).balanceOf(address(this));

        TokenTransfer.sendTokens(tokenAddress, isNativeToken, beneficiary, amount);

        emit FundsReleased(beneficiary, amount);
    }

    /**
     * @notice Enables refunds if goal was not met by deadline
     * @dev Only creator can enable refunds, only after deadline if goal not met
     */
    function enableRefunds() external onlyCreator whenNotPaused {
        require(!fundsReleased, "Funds already released");
        require(!refundsEnabled, "Refunds already enabled");
        require(block.timestamp > deadline, "Deadline not passed");
        require(totalRaised < goalAmount, "Goal was met");

        refundsEnabled = true;
        isActive = false;

        emit RefundsEnabled(block.timestamp);
    }

    /**
     * @notice Allows a donor to claim their refund
     * @dev Only available if refunds are enabled
     */
    function claimRefund() external whenNotPaused nonReentrant {
        require(refundsEnabled, "Refunds not enabled");
        require(donations[msg.sender] > 0, "Nothing to refund");

        uint256 amount = donations[msg.sender];
        donations[msg.sender] = 0;
        totalRaised -= amount;

        TokenTransfer.sendTokens(tokenAddress, isNativeToken, msg.sender, amount);

        emit RefundClaimed(msg.sender, amount);
    }

    // ========== Emergency Functions ==========

    /**
     * @notice Pauses the contract in case of emergency
     * @dev Only creator can pause
     */
    function pause() external onlyCreator {
        _pause();
    }

    /**
     * @notice Unpauses the contract
     * @dev Only creator can unpause
     */
    function unpause() external onlyCreator {
        _unpause();
    }

    // ========== View Functions ==========

    /**
     * @notice Gets a member's total donation amount
     * @param donor Address of the donor
     * @return Donation amount in wei
     */
    function getDonation(address donor) external view returns (uint256) {
        return donations[donor];
    }

    /**
     * @notice Gets the total number of members
     * @return Member count
     */
    function getMemberCount() external view returns (uint256) {
        return members.length;
    }

    /**
     * @notice Gets all members
     * @return Array of member addresses
     */
    function getMembers() external view returns (address[] memory) {
        return members;
    }

    /**
     * @notice Gets the total number of donors
     * @return Donor count
     */
    function getDonorCount() external view returns (uint256) {
        return donors.length;
    }

    /**
     * @notice Gets all donors
     * @return Array of donor addresses
     */
    function getDonors() external view returns (address[] memory) {
        return donors;
    }

    /**
     * @notice Gets the time remaining until deadline
     * @return Seconds remaining, or 0 if deadline passed
     */
    function getTimeRemaining() external view returns (uint256) {
        if (block.timestamp >= deadline) {
            return 0;
        }
        return deadline - block.timestamp;
    }

    /**
     * @notice Gets the current fundraising progress
     * @return raised Current amount raised
     * @return goal Goal amount
     */
    function getProgress() external view returns (uint256 raised, uint256 goal) {
        return (totalRaised, goalAmount);
    }

    /**
     * @notice Checks if the fundraising goal has been reached
     * @return True if goal reached, false otherwise
     */
    function isGoalReached() external view returns (bool) {
        return totalRaised >= goalAmount;
    }

    /**
     * @notice Gets the contract's current balance
     * @return Balance in wei
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

contracts/pools/ROSCAPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../interfaces/IXershaPool.sol";
import "../libraries/TokenTransfer.sol";

/**
 * @title ROSCAPool
 * @notice Rotating Savings and Credit Association pool implementation
 * @dev Members contribute fixed amounts each round, one member receives the pot each round
 * Payout order is provided off-chain by the creator for fairness and transparency
 */
contract ROSCAPool is IXershaPool, ReentrancyGuard, Pausable {
    using TokenTransfer for address;

    // ========== Constants ==========

    /// @notice Duration of each cycle/round in seconds (30 days)
    uint256 public constant CYCLE_DURATION = 30 days;

    /// @notice Minimum number of members required to start a ROSCA
    uint8 public constant MIN_MEMBERS = 5;

    /// @notice Maximum number of members allowed in a ROSCA
    uint8 public constant MAX_MEMBERS = 12;

    // ========== State Variables ==========

    /// @notice Address of the user who created this pool
    address public creator;

    /// @notice Address of the backend manager (can invite members)
    address public backendManager;

    /// @notice Address of the Lens.xyz circle contract
    address public circleId;

    /// @notice Human-readable name of the circle
    string public circleName;

    /// @notice Address of the ERC20 token used for contributions (zero address if native token)
    address public tokenAddress;

    /// @notice Whether this pool uses native token (cBTC) or ERC20 token
    bool public isNativeToken;

    /// @notice Fixed contribution amount per round in wei
    uint256 public contributionAmount;

    /// @notice Array of all members in the pool
    address[] public members;

    /// @notice Mapping to check if an address is a member
    mapping(address => bool) public isMember;

    /// @notice Mapping to check if an address has been invited
    mapping(address => bool) public isInvited;

    /// @notice Total amount contributed by each member across all rounds
    mapping(address => uint256) public totalContributed;

    /// @notice Array defining the order in which members receive payouts
    address[] public payoutOrder;

    /// @notice Current round number (1-indexed)
    uint8 public currentRound;

    /// @notice Whether the current round's payout has been completed
    bool public currentRoundPaidOut;

    /// @notice Mapping to track which members have received their payout
    mapping(address => bool) public hasReceivedPayout;

    /// @notice Tracks whether a member has paid for a specific round
    mapping(address => mapping(uint8 => bool)) public hasPaid;

    /// @notice Timestamp when the ROSCA was started
    uint256 public roscaStartTime;

    /// @notice Timestamp when the current round started
    uint256 public currentRoundStartTime;

    /// @notice Whether the ROSCA is currently active
    bool public isActive;

    /// @notice Whether the ROSCA has completed all rounds
    bool public isComplete;

    /// @notice Whether this contract has been initialized (for clone pattern)
    bool private initialized;

    // ========== Events ==========

    event ROSCACreated(address indexed circleId, address indexed creator, uint256 contributionAmount);
    event MemberInvited(address indexed member, address indexed invitedBy);
    event MemberJoined(address indexed member, uint256 timestamp);
    event ROSCAStarted(address[] payoutOrder, uint256 startTime);
    event ContributionMade(address indexed member, uint8 round, uint256 amount);
    event AllMembersContributed(uint8 round);
    event PayoutTriggered(address indexed recipient, uint256 amount, uint8 round);
    event RoundStarted(uint8 round, uint256 startTime);
    event ROSCACompleted(uint256 completionTime);

    // ========== Modifiers ==========

    modifier onlyCreator() {
        require(msg.sender == creator, "Only creator can call this");
        _;
    }

    modifier onlyCreatorOrBackend() {
        require(
            msg.sender == creator || msg.sender == backendManager,
            "Only creator or backend"
        );
        _;
    }

    modifier onlyMember() {
        require(isMember[msg.sender], "Not a member");
        _;
    }

    modifier onlyInvited() {
        require(isInvited[msg.sender], "Not invited");
        _;
    }

    modifier poolActive() {
        require(isActive && !isComplete, "Pool not active");
        _;
    }

    // ========== Constructor ==========

    /**
     * @notice Constructor for implementation contract
     * @dev Prevents the implementation contract from being initialized
     */
    constructor() {
        initialized = true;
    }

    /**
     * @notice Initializes a new ROSCA pool clone
     * @dev This replaces the constructor for cloned instances
     * @param _creator Address of the user creating the pool
     * @param _circleId Address of the Lens.xyz circle contract
     * @param _circleName Name of the circle
     * @param _backendManager Address of the backend manager (can invite members)
     * @param _contributionAmount Fixed contribution amount per round
     * @param _tokenAddress Address of the ERC20 token to use for contributions (zero address if native)
     * @param _isNativeToken Whether this pool uses native token (cBTC) or ERC20 token
     */
    function initialize(
        address _creator,
        address _circleId,
        string memory _circleName,
        address _backendManager,
        uint256 _contributionAmount,
        address _tokenAddress,
        bool _isNativeToken
    ) external {
        require(!initialized, "Already initialized");
        initialized = true;

        require(_contributionAmount > 0, "Invalid contribution amount");
        require(_backendManager != address(0), "Invalid backend manager");

        // Validate token address based on token type
        if (_isNativeToken) {
            require(_tokenAddress == address(0), "Token address must be zero for native token");
        } else {
            require(_tokenAddress != address(0), "Invalid token address for ERC20");
        }

        creator = _creator;
        backendManager = _backendManager;
        circleId = _circleId;
        circleName = _circleName;
        contributionAmount = _contributionAmount;
        tokenAddress = _tokenAddress;
        isNativeToken = _isNativeToken;

        // Creator automatically becomes a member
        members.push(_creator);
        isMember[_creator] = true;
        isInvited[_creator] = true;

        emit ROSCACreated(_circleId, _creator, _contributionAmount);
        emit MemberJoined(_creator, block.timestamp);
    }

    // ========== Member Management ==========

    /**
     * @notice Invites a new member to the ROSCA
     * @dev Creator or backend manager can invite, only before ROSCA starts
     * @param member Address of the member to invite
     */
    function inviteMember(address member) external onlyCreatorOrBackend whenNotPaused {
        require(!isActive, "Cannot invite after ROSCA starts");
        require(!isInvited[member], "Already invited");
        require(members.length < MAX_MEMBERS, "Max members reached");

        isInvited[member] = true;
        emit MemberInvited(member, msg.sender);
    }

    /**
     * @notice Allows an invited member to join the pool
     * @dev Can only join before ROSCA starts
     */
    function joinPool() external onlyInvited whenNotPaused {
        require(!isActive, "Cannot join after ROSCA starts");
        require(!isMember[msg.sender], "Already a member");

        members.push(msg.sender);
        isMember[msg.sender] = true;

        emit MemberJoined(msg.sender, block.timestamp);
    }

    // ========== ROSCA Lifecycle ==========

    /**
     * @notice Starts the ROSCA with a provided payout order
     * @dev Only creator can start, requires min members, payout order generated off-chain
     * @param _payoutOrder Array of addresses defining payout order (must include all members exactly once)
     */
    function startROSCA(address[] calldata _payoutOrder) external onlyCreator whenNotPaused {
        require(!isActive, "Already started");
        require(members.length >= MIN_MEMBERS, "Not enough members");
        require(_payoutOrder.length == members.length, "Invalid payout order length");

        // Validate payout order contains all members exactly once
        _validatePayoutOrder(_payoutOrder);

        payoutOrder = _payoutOrder;
        isActive = true;
        currentRound = 1;
        roscaStartTime = block.timestamp;
        currentRoundStartTime = block.timestamp;

        emit ROSCAStarted(_payoutOrder, block.timestamp);
        emit RoundStarted(1, block.timestamp);
    }

    /**
     * @notice Validates that payout order contains all members exactly once
     * @param _payoutOrder The payout order array to validate
     */
    function _validatePayoutOrder(address[] calldata _payoutOrder) private view {
        // Validate each member is in the payout order and check for duplicates
        for (uint256 i = 0; i < _payoutOrder.length; i++) {
            address recipient = _payoutOrder[i];
            require(isMember[recipient], "Payout order contains non-member");

            // Check for duplicates by using a simple loop (gas-efficient for small arrays)
            for (uint256 j = i + 1; j < _payoutOrder.length; j++) {
                require(_payoutOrder[i] != _payoutOrder[j], "Duplicate in payout order");
            }
        }
    }

    /**
     * @notice Allows a member to contribute for the current round
     * @dev For ERC20: Member must have approved the contract to spend tokens before calling
     *      For native token: Must send exact amount of native currency
     */
    function contribute() external payable onlyMember poolActive whenNotPaused nonReentrant {
        require(!hasPaid[msg.sender][currentRound], "Already contributed");
        require(currentRound <= members.length, "All rounds complete");

        hasPaid[msg.sender][currentRound] = true;
        totalContributed[msg.sender] += contributionAmount;

        TokenTransfer.receiveTokens(tokenAddress, isNativeToken, contributionAmount);

        emit ContributionMade(msg.sender, currentRound, contributionAmount);

        if (_everyonePaid()) {
            emit AllMembersContributed(currentRound);
        }
    }

    /**
     * @notice Triggers the payout for the current round
     * @dev Only the designated recipient can trigger their own payout
     * Requires all members to have contributed for the current round
     */
    function triggerPayout() external poolActive whenNotPaused nonReentrant {
        require(_everyonePaid(), "Not everyone has paid");
        require(!currentRoundPaidOut, "Round already paid out");

        address recipient = payoutOrder[currentRound - 1];
        require(msg.sender == recipient, "Only recipient can claim payout");

        currentRoundPaidOut = true;
        hasReceivedPayout[recipient] = true;

        uint256 payoutAmount = contributionAmount * members.length;

        TokenTransfer.sendTokens(tokenAddress, isNativeToken, recipient, payoutAmount);

        emit PayoutTriggered(recipient, payoutAmount, currentRound);

        // Check if ROSCA is complete
        if (currentRound >= members.length) {
            isComplete = true;
            isActive = false;
            emit ROSCACompleted(block.timestamp);
        }
    }

    /**
     * @notice Starts the next round after the cycle duration has passed
     * @dev Any member can call this after the 30-day cycle is complete
     */
    function startNextRound() external onlyMember whenNotPaused {
        require(isActive && !isComplete, "ROSCA not active");
        require(currentRoundPaidOut, "Current round not paid out");
        require(currentRound < members.length, "All rounds complete");
        require(
            block.timestamp >= currentRoundStartTime + CYCLE_DURATION,
            "30-day cycle not complete"
        );

        currentRound++;
        currentRoundStartTime = block.timestamp;
        currentRoundPaidOut = false;

        emit RoundStarted(currentRound, block.timestamp);
    }

    // ========== Emergency Functions ==========

    /**
     * @notice Pauses the contract in case of emergency
     * @dev Only creator can pause
     */
    function pause() external onlyCreator {
        _pause();
    }

    /**
     * @notice Unpauses the contract
     * @dev Only creator can unpause
     */
    function unpause() external onlyCreator {
        _unpause();
    }

    // ========== View Functions ==========

    /**
     * @notice Checks if everyone has paid for the current round
     * @return True if all members have contributed, false otherwise
     */
    function everyonePaid() external view returns (bool) {
        return _everyonePaid();
    }

    /**
     * @notice Internal function to check if everyone has paid
     * @return True if all members have contributed, false otherwise
     */
    function _everyonePaid() private view returns (bool) {
        for (uint256 i = 0; i < members.length; i++) {
            if (!hasPaid[members[i]][currentRound]) {
                return false;
            }
        }
        return true;
    }

    /**
     * @notice Gets the current round's recipient
     * @return Address of the member who should receive payout this round
     */
    function getCurrentRecipient() external view returns (address) {
        if (currentRound == 0 || currentRound > payoutOrder.length) {
            return address(0);
        }
        return payoutOrder[currentRound - 1];
    }

    /**
     * @notice Gets the total number of members
     * @return Member count
     */
    function getMemberCount() external view returns (uint256) {
        return members.length;
    }

    /**
     * @notice Gets all members
     * @return Array of member addresses
     */
    function getMembers() external view returns (address[] memory) {
        return members;
    }

    /**
     * @notice Gets the complete payout order
     * @return Array of addresses in payout order
     */
    function getPayoutOrder() external view returns (address[] memory) {
        return payoutOrder;
    }

    /**
     * @notice Gets list of members who have contributed in the current round
     * @return Array of addresses who have paid
     */
    function getRoundContributors() external view returns (address[] memory) {
        uint256 contributorCount = 0;

        // Count contributors
        for (uint256 i = 0; i < members.length; i++) {
            if (hasPaid[members[i]][currentRound]) {
                contributorCount++;
            }
        }

        // Build contributor array
        address[] memory contributors = new address[](contributorCount);
        uint256 index = 0;

        for (uint256 i = 0; i < members.length; i++) {
            if (hasPaid[members[i]][currentRound]) {
                contributors[index] = members[i];
                index++;
            }
        }

        return contributors;
    }

    /**
     * @notice Gets the contract's current balance
     * @return Balance in wei
     */
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

contracts/pools/SavingsPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../interfaces/IXershaPool.sol";
import "../libraries/TokenTransfer.sol";

/**
 * @title SavingsPool
 * @notice Collective savings pool where members can deposit and withdraw freely
 * @dev Members maintain individual balances and can set collective savings goals
 */
contract SavingsPool is IXershaPool, ReentrancyGuard, Pausable {
    using TokenTransfer for address;

    // ========== State Variables ==========

    /// @notice Address of the user who created this pool
    address public creator;

    /// @notice Address of the backend manager (can invite members)
    address public backendManager;

    /// @notice Address of the Lens.xyz circle contract
    address public circleId;

    /// @notice Human-readable name of the circle
    string public circleName;

    /// @notice Address of the ERC20 token used for savings (zero address if native token)
    address public tokenAddress;

    /// @notice Whether this pool uses native token (cBTC) or ERC20 token
    bool public isNativeToken;

    /// @notice Individual balances for each member
    mapping(address => uint256) public balances;

    /// @notice Total amount saved across all members
    uint256 public totalSaved;

    /// @notice Array of all members in the pool
    address[] public members;

    /// @notice Mapping to check if an address is a member
    mapping(address => bool) public isMember;

    /// @notice Mapping to check if an address has been invited
    mapping(address => bool) public isInvited;

    /// @notice Optional target savings amount set by creator
    uint256 public targetAmount;

    /// @notice Optional target date for reaching the goal
    uint256 public targetDate;

    /// @notice Whether the pool is currently active
    bool public isActive;

    /// @notice Whether this contract has been initialized (for clone pattern)
    bool private initialized;

    // ========== Events ==========

    event PoolCreated(address indexed circleId, address indexed creator);
    event MemberInvited(address indexed member, address indexed invitedBy);
    event MemberJoined(address indexed member);
    event Deposited(address indexed member, uint256 amount);
    event Withdrawn(address indexed member, uint256 amount);
    event TargetSet(uint256 amount, uint256 date);
    event PoolClosed(uint256 timestamp);

    // ========== Modifiers ==========

    modifier onlyCreator() {
        require(msg.sender == creator, "Only creator can call this");
        _;
    }

    modifier onlyCreatorOrBackend() {
        require(
            msg.sender == creator || msg.sender == backendManager,
            "Only creator or backend"
        );
        _;
    }

    modifier onlyMember() {
        require(isMember[msg.sender], "Not a member");
        _;
    }

    modifier onlyInvited() {
        require(isInvited[msg.sender], "Not invited");
        _;
    }

    modifier poolIsActive() {
        require(isActive, "Pool not active");
        _;
    }

    // ========== Constructor ==========

    /**
     * @notice Constructor for implementation contract
     * @dev Prevents the implementation contract from being initialized
     */
    constructor() {
        initialized = true;
    }

    /**
     * @notice Initializes a new Savings pool clone
     * @dev This replaces the constructor for cloned instances
     * @param _creator Address of the user creating the pool
     * @param _circleId Address of the Lens.xyz circle contract
     * @param _circleName Name of the circle
     * @param _backendManager Address of the backend manager (can invite members)
     * @param _tokenAddress Address of the ERC20 token to use for savings (zero address if native)
     * @param _isNativeToken Whether this pool uses native token (cBTC) or ERC20 token
     */
    function initialize(
        address _creator,
        address _circleId,
        string memory _circleName,
        address _backendManager,
        address _tokenAddress,
        bool _isNativeToken
    ) external {
        require(!initialized, "Already initialized");
        initialized = true;

        // Validate token address based on token type
        if (_isNativeToken) {
            require(_tokenAddress == address(0), "Token address must be zero for native token");
        } else {
            require(_tokenAddress != address(0), "Invalid token address for ERC20");
        }

        require(_backendManager != address(0), "Invalid backend manager");

        creator = _creator;
        backendManager = _backendManager;
        circleId = _circleId;
        circleName = _circleName;
        tokenAddress = _tokenAddress;
        isNativeToken = _isNativeToken;
        isActive = true;

        // Creator automatically becomes a member
        members.push(_creator);
        isMember[_creator] = true;
        isInvited[_creator] = true;

        emit PoolCreated(_circleId, _creator);
        emit MemberJoined(_creator);
    }

    // ========== Member Management ==========

    /**
     * @notice Invites a new member to the pool
     * @dev Creator or backend manager can invite members
     * @param member Address of the member to invite
     */
    function inviteMember(address member) external onlyCreatorOrBackend whenNotPaused {
        require(!isInvited[member], "Already invited");
        require(member != address(0), "Invalid address");

        isInvited[member] = true;
        emit MemberInvited(member, msg.sender);
    }

    /**
     * @notice Allows an invited member to join the pool
     */
    function joinPool() external onlyInvited whenNotPaused {
        require(!isMember[msg.sender], "Already a member");

        members.push(msg.sender);
        isMember[msg.sender] = true;

        emit MemberJoined(msg.sender);
    }

    // ========== Deposits & Withdrawals ==========

    /**
     * @notice Allows a member to deposit funds into their savings
     * @dev For ERC20: Member must have approved the contract to spend tokens before calling
     *      For native token: amount parameter is ignored, msg.value is used
     * @param amount Amount of ERC20 tokens to deposit (ignored for native token pools)
     */
    function deposit(uint256 amount) external payable onlyMember poolIsActive whenNotPaused nonReentrant {
        require(isNativeToken || amount > 0, "Must deposit something");

        uint256 depositAmount = TokenTransfer.receiveTokens(tokenAddress, isNativeToken, amount);

        balances[msg.sender] += depositAmount;
        totalSaved += depositAmount;

        emit Deposited(msg.sender, depositAmount);
    }

    /**
     * @notice Allows a member to withdraw from their savings balance
     * @param amount Amount of tokens to withdraw
     */
    function withdraw(uint256 amount) external onlyMember whenNotPaused nonReentrant {
        require(amount > 0, "Must withdraw something");
        require(balances[msg.sender] >= amount, "Insufficient balance");

        balances[msg.sender] -= amount;
        totalSaved -= amount;

        TokenTransfer.sendTokens(tokenAddress, isNativeToken, msg.sender, amount);

        emit Withdrawn(msg.sender, amount);
    }

    // ========== Goal Management ==========

    /**
     * @notice Sets or updates the savings goal for the pool
     * @dev Only creator can set goals
     * @param _amount Target savings amount in wei
     * @param _date Target date as Unix timestamp
     */
    function setTarget(uint256 _amount, uint256 _date) external onlyCreator whenNotPaused {
        require(_amount > 0, "Target amount must be positive");
        require(_date > block.timestamp, "Target date must be in future");

        targetAmount = _amount;
        targetDate = _date;

        emit TargetSet(_amount, _date);
    }

    // ========== Pool Management ==========

    /**
     * @notice Closes the pool, preventing further deposits
     * @dev Only creator can close. Members can still withdraw their balances.
     */
    function closePool() external onlyCreator whenNotPaused {
        isActive = false;
        emit PoolClosed(block.timestamp);
    }

    /**
     * @notice Pauses the contract in case of emergency
     * @dev Only creator can pause
     */
    function pause() external onlyCreator {
        _pause();
    }

    /**
     * @notice Unpauses the contract
     * @dev Only creator can unpause
     */
    function unpause() external onlyCreator {
        _unpause();
    }

    // ========== View Functions ==========

    /**
     * @notice Gets a member's savings balance
     * @param member Address of the member
     * @return Balance in wei
     */
    function getBalance(address member) external view returns (uint256) {
        return balances[member];
    }

    /**
     * @notice Gets the total number of members
     * @return Member count
     */
    function getMemberCount() external view returns (uint256) {
        return members.length;
    }

    /**
     * @notice Gets all members
     * @return Array of member addresses
     */
    function getMembers() external view returns (address[] memory) {
        return members;
    }

    /**
     * @notice Gets the current progress toward the savings goal
     * @return current Current total savings
     * @return target Target savings amount
     */
    function getProgress() external view returns (uint256 current, uint256 target) {
        return (totalSaved, targetAmount);
    }

    /**
     * @notice Checks if the savings goal has been reached
     * @return True if target is set and reached, false otherwise
     */
    function isGoalReached() external view returns (bool) {
        if (targetAmount == 0) return false;
        return totalSaved >= targetAmount;
    }

    /**
     * @notice Gets the contract's current balance
     * @return Balance in wei
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

contracts/XershaFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./pools/ROSCAPool.sol";
import "./pools/SavingsPool.sol";
import "./pools/DonationPool.sol";

/**
 * @title XershaFactory
 * @notice Factory contract for creating and managing Xersha pools using minimal proxy pattern
 * @dev Enforces one pool per circle and tracks all deployed pools
 *      Uses EIP-1167 minimal proxies (clones) for gas-efficient pool deployment
 */
contract XershaFactory is Ownable {
    // ========== Types ==========

    enum PoolType {
        ROSCA,
        SAVINGS,
        DONATION
    }

    // ========== State Variables ==========

    /// @notice Implementation contract for ROSCA pools
    address public roscaImplementation;

    /// @notice Implementation contract for Savings pools
    address public savingsImplementation;

    /// @notice Implementation contract for Donation pools
    address public donationImplementation;

    /// @notice Address of the backend manager for all pools
    address public backendManager;

    /// @notice Mapping from circle contract address to pool address
    mapping(address => address) public circleToPool;

    /// @notice Mapping to validate if an address is a legitimate pool created by this factory
    mapping(address => bool) public isValidPool;

    /// @notice Mapping from pool address to pool type
    mapping(address => PoolType) public poolTypes;

    /// @notice Array of all pools created by this factory
    address[] public allPools;

    // ========== Events ==========

    /**
     * @notice Emitted when a new pool is created
     * @param circleId The Lens.xyz circle contract address
     * @param poolAddress The address of the newly created pool
     * @param creator The address that created the pool
     * @param poolType The type of pool created (ROSCA, SAVINGS, or DONATION)
     */
    event PoolCreated(
        address indexed circleId,
        address indexed poolAddress,
        address indexed creator,
        PoolType poolType
    );

    /**
     * @notice Emitted when an implementation address is updated
     * @param poolType The type of pool implementation that was updated
     * @param newImplementation The new implementation address
     */
    event ImplementationUpdated(string poolType, address indexed newImplementation);

    /**
     * @notice Emitted when the backend manager address is updated
     * @param oldManager The previous backend manager address
     * @param newManager The new backend manager address
     */
    event BackendManagerUpdated(address indexed oldManager, address indexed newManager);

    // ========== Constructor ==========

    /**
     * @notice Initializes the factory with implementation addresses
     * @param initialOwner Address that will own the factory
     * @param _backendManager Address of the backend manager for all pools
     * @param _roscaImpl Address of the ROSCA pool implementation
     * @param _savingsImpl Address of the Savings pool implementation
     * @param _donationImpl Address of the Donation pool implementation
     */
    constructor(
        address initialOwner,
        address _backendManager,
        address _roscaImpl,
        address _savingsImpl,
        address _donationImpl
    ) Ownable(initialOwner) {
        require(_backendManager != address(0), "Invalid backend manager");
        require(_roscaImpl != address(0), "Invalid ROSCA implementation");
        require(_savingsImpl != address(0), "Invalid Savings implementation");
        require(_donationImpl != address(0), "Invalid Donation implementation");

        backendManager = _backendManager;
        roscaImplementation = _roscaImpl;
        savingsImplementation = _savingsImpl;
        donationImplementation = _donationImpl;
    }

    // ========== Pool Creation Functions ==========

    /**
     * @notice Creates a new ROSCA (Rotating Savings and Credit Association) pool
     * @dev Validates circle ID and ensures no duplicate pools for the same circle
     *      Uses EIP-1167 minimal proxy pattern for gas-efficient deployment
     * @param circleId The Lens.xyz circle contract address
     * @param circleName The name of the circle
     * @param contributionAmount Fixed amount each member must contribute per round
     * @param tokenAddress Address of the ERC20 token (zero address if native)
     * @param isNativeToken Whether to use native token or ERC20
     * @return The address of the newly created ROSCA pool clone
     */
    function createROSCA(
        address circleId,
        string memory circleName,
        uint256 contributionAmount,
        address tokenAddress,
        bool isNativeToken
    ) external returns (address) {
        _validateCircleId(circleId);
        require(circleToPool[circleId] == address(0), "Circle already has pool");
        require(contributionAmount > 0, "Invalid contribution amount");

        // Clone the ROSCA implementation
        address clone = Clones.clone(roscaImplementation);

        // Initialize the clone
        ROSCAPool(clone).initialize(
            msg.sender,
            circleId,
            circleName,
            backendManager,
            contributionAmount,
            tokenAddress,
            isNativeToken
        );

        _registerPool(circleId, clone, PoolType.ROSCA);

        emit PoolCreated(circleId, clone, msg.sender, PoolType.ROSCA);
        return clone;
    }

    /**
     * @notice Creates a new Savings pool for collective savings
     * @dev Validates circle ID and ensures no duplicate pools for the same circle
     *      Uses EIP-1167 minimal proxy pattern for gas-efficient deployment
     * @param circleId The Lens.xyz circle contract address
     * @param circleName The name of the circle
     * @param tokenAddress Address of the ERC20 token (zero address if native)
     * @param isNativeToken Whether to use native token or ERC20
     * @return The address of the newly created Savings pool clone
     */
    function createSavingsPool(
        address circleId,
        string memory circleName,
        address tokenAddress,
        bool isNativeToken
    ) external returns (address) {
        _validateCircleId(circleId);
        require(circleToPool[circleId] == address(0), "Circle already has pool");

        // Clone the Savings implementation
        address clone = Clones.clone(savingsImplementation);

        // Initialize the clone
        SavingsPool(clone).initialize(
            msg.sender,
            circleId,
            circleName,
            backendManager,
            tokenAddress,
            isNativeToken
        );

        _registerPool(circleId, clone, PoolType.SAVINGS);

        emit PoolCreated(circleId, clone, msg.sender, PoolType.SAVINGS);
        return clone;
    }

    /**
     * @notice Creates a new Donation pool for group fundraising
     * @dev Validates circle ID and ensures no duplicate pools for the same circle
     *      Uses EIP-1167 minimal proxy pattern for gas-efficient deployment
     * @param circleId The Lens.xyz circle contract address
     * @param circleName The name of the circle
     * @param beneficiary The address that will receive the donated funds
     * @param goalAmount The fundraising goal amount in wei
     * @param deadline Unix timestamp when fundraising ends
     * @param tokenAddress Address of the ERC20 token (zero address if native)
     * @param isNativeToken Whether to use native token or ERC20
     * @return The address of the newly created Donation pool clone
     */
    function createDonationPool(
        address circleId,
        string memory circleName,
        address beneficiary,
        uint256 goalAmount,
        uint256 deadline,
        address tokenAddress,
        bool isNativeToken
    ) external returns (address) {
        _validateCircleId(circleId);
        require(circleToPool[circleId] == address(0), "Circle already has pool");
        require(beneficiary != address(0), "Invalid beneficiary");
        require(goalAmount > 0, "Invalid goal");
        require(deadline > block.timestamp, "Invalid deadline");

        // Clone the Donation implementation
        address clone = Clones.clone(donationImplementation);

        // Initialize the clone
        DonationPool(clone).initialize(
            msg.sender,
            circleId,
            circleName,
            backendManager,
            beneficiary,
            goalAmount,
            deadline,
            tokenAddress,
            isNativeToken
        );

        _registerPool(circleId, clone, PoolType.DONATION);

        emit PoolCreated(circleId, clone, msg.sender, PoolType.DONATION);
        return clone;
    }

    // ========== Admin Functions ==========

    /**
     * @notice Updates the ROSCA pool implementation address
     * @dev Only owner can update. New pools will use the new implementation
     * @param newImplementation Address of the new ROSCA implementation
     */
    function setROSCAImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "Invalid implementation");
        roscaImplementation = newImplementation;
        emit ImplementationUpdated("ROSCA", newImplementation);
    }

    /**
     * @notice Updates the Savings pool implementation address
     * @dev Only owner can update. New pools will use the new implementation
     * @param newImplementation Address of the new Savings implementation
     */
    function setSavingsImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "Invalid implementation");
        savingsImplementation = newImplementation;
        emit ImplementationUpdated("SAVINGS", newImplementation);
    }

    /**
     * @notice Updates the Donation pool implementation address
     * @dev Only owner can update. New pools will use the new implementation
     * @param newImplementation Address of the new Donation implementation
     */
    function setDonationImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "Invalid implementation");
        donationImplementation = newImplementation;
        emit ImplementationUpdated("DONATION", newImplementation);
    }

    /**
     * @notice Updates all implementation addresses at once
     * @dev Only owner can update. Pass address(0) to skip updating a specific implementation
     * @param _roscaImpl New ROSCA implementation (or address(0) to skip)
     * @param _savingsImpl New Savings implementation (or address(0) to skip)
     * @param _donationImpl New Donation implementation (or address(0) to skip)
     */
    function setImplementations(
        address _roscaImpl,
        address _savingsImpl,
        address _donationImpl
    ) external onlyOwner {
        if (_roscaImpl != address(0)) {
            roscaImplementation = _roscaImpl;
            emit ImplementationUpdated("ROSCA", _roscaImpl);
        }
        if (_savingsImpl != address(0)) {
            savingsImplementation = _savingsImpl;
            emit ImplementationUpdated("SAVINGS", _savingsImpl);
        }
        if (_donationImpl != address(0)) {
            donationImplementation = _donationImpl;
            emit ImplementationUpdated("DONATION", _donationImpl);
        }
    }

    /**
     * @notice Updates the backend manager address for all future pools
     * @dev Only owner can update. Existing pools are not affected.
     * @param _backendManager New backend manager address
     */
    function setBackendManager(address _backendManager) external onlyOwner {
        require(_backendManager != address(0), "Invalid address");
        address oldManager = backendManager;
        backendManager = _backendManager;
        emit BackendManagerUpdated(oldManager, _backendManager);
    }

    // ========== Internal Functions ==========

    /**
     * @notice Validates that the circle ID is not the zero address
     * @dev Circle IDs can reference contracts on other chains (e.g., Lens circles on Polygon)
     * @param circleId The circle address to validate
     */
    function _validateCircleId(address circleId) private pure {
        require(circleId != address(0), "Zero address not allowed");
    }

    /**
     * @notice Registers a newly created pool in the factory's tracking systems
     * @param circleId The circle contract address
     * @param poolAddress The address of the newly created pool
     * @param poolType The type of pool (ROSCA, SAVINGS, or DONATION)
     */
    function _registerPool(address circleId, address poolAddress, PoolType poolType) private {
        circleToPool[circleId] = poolAddress;
        isValidPool[poolAddress] = true;
        poolTypes[poolAddress] = poolType;
        allPools.push(poolAddress);
    }

    // ========== View Functions ==========

    /**
     * @notice Gets the pool address for a given circle
     * @param circleId The circle contract address
     * @return The pool address, or zero address if no pool exists
     */
    function getCirclePool(address circleId) external view returns (address) {
        return circleToPool[circleId];
    }

    /**
     * @notice Gets the total number of pools created by this factory
     * @return The total pool count
     */
    function getTotalPools() external view returns (uint256) {
        return allPools.length;
    }

    /**
     * @notice Gets all pool addresses created by this factory
     * @dev Warning: This can be gas-intensive for large numbers of pools
     * @return Array of all pool addresses
     */
    function getAllPools() external view returns (address[] memory) {
        return allPools;
    }

    /**
     * @notice Gets the pool type for a given pool address
     * @param poolAddress The pool address to query
     * @return The PoolType enum value
     */
    function getPoolType(address poolAddress) external view returns (PoolType) {
        require(isValidPool[poolAddress], "Invalid pool address");
        return poolTypes[poolAddress];
    }
}
